mapper.select:
		/* admin.user.selectPageListUser */


					SELECT AA.*
					FROM(






   						ORDER BY A.USER_NM ASC
					) AA
				LIMIT #{limitStart},#{limitEnd}

mapper.insert:
/* admin.user.insertUser */

INSERT INTO op_user (
	USER_NO
	, USER_ID
	, PASSWORD
	, AREA_CODE
	, AREA_SE_CODE_S
	, AREA_SE_CODE_D
	, USER_NM
	, CTTPC_SE_CODE
	, CTTPC
	, EMAIL
	, USER_STTUS_CODE
	, REGISTER_NO
	, REGIST_DT
) VALUES (
	  #{user_no}
	, #{user_id}
	, #{enPwd}
	, #{area_se_code_m}
	, #{area_se_code_s}
	, #{area_se_code_d}
	, #{user_nm}
	, #{cttpc_se_code}
	, #{cttpc}
	, #{email}
	, #{user_sttus_code}
	, #{ss_user_no}
	, CURRENT_TIMESTAMP
)

INSERT INTO op_user (
	USER_NO
	, USER_ID
	, PASSWORD
	, AREA_CODE
	, AREA_SE_CODE_S
	, AREA_SE_CODE_D
	, USER_NM
	, CTTPC_SE_CODE
	, CTTPC
	, EMAIL
	, USER_STTUS_CODE
	, TMPR_PASSWORD_ISSU_YN
	, REGISTER_NO
	, REGIST_DT
) VALUES (
	(select * from (select ifnull(max(USER_NO),0)+1 from op_user) next)
	, #{user_id}
	, #{enPwd}
	, #{area_se_code_m}
	, #{area_se_code_s}
	, #{area_se_code_d}
	, #{user_nm}
	, #{cttpc_se_code}
	, #{cttpc}
	, #{email}
	, #{user_sttus_code}
	, 'N'
	, #{user_no}
	, CURRENT_TIMESTAMP
)

/* admin.user.insertUserChgHst */

	INSERT INTO op_user_chghst(
