			(select * from (select ifnull(max(USER_CHGHST_SEQ),0)+1 from op_user_chghst) next)
			,#{user_no}
			,'R010280'
	        ,#{user_change_code_m}
			,#{change_cn}
			, #{ss_user_no}
			, CURRENT_TIMESTAMP
			, #{ss_user_no}
			, CURRENT_TIMESTAMP
	)

mapper.update:
/* admin.user.updateUser */
UPDATE op_user
SET USER_NM = #{user_nm}
	, USER_STTUS_CODE = #{user_sttus_code}
	, CTTPC_SE_CODE = #{cttpc_se_code}
	, CTTPC = #{cttpc}
	, EMAIL = #{email}
	, AREA_CODE = #{area_se_code_m}
	, AREA_SE_CODE_S = #{area_se_code_s}
	, AREA_SE_CODE_D = #{area_se_code_d}

	, PASSWORD = #{enPwd}

	, UPDUSR_NO 	= #{ss_user_no}
	, UPDT_DT 		= CURRENT_TIMESTAMP
WHERE USER_NO = #{user_no}

/* admin.user.updateUserPsssword */
UPDATE op_user
SET	  PASSWORD 	= #{enPwd}
		, UPDUSR_NO 	= #{user_no}
		, UPDT_DT 		= CURRENT_TIMESTAMP
WHERE USER_NO 		= #{user_no}

/* admin.user.updateUserInfo */
UPDATE op_user
SET UPDUSR_NO = #{ss_user_no}

	, USER_NM = #{modal_user_nm} 


	, CTTPC_SE_CODE = #{modal_cttpc_se_code}


	, CTTPC = #{modal_cttpc}


	, EMAIL = #{modal_email}


	, PASSWORD = #{enPwd}

	, UPDT_DT = CURRENT_TIMESTAMP
WHERE USER_NO = #{modal_user_no}

mapper.delete:
/* admin.user.deleteUser */

UPDATE op_user
SET USER_STTUS_CODE = #{user_sttus_code_stop}
	, UPDT_DT = CURRENT_TIMESTAMP
WHERE USER_NO = #{user_no}

/* admin.user.deleteAuthUser */

DELETE 
FROM op_user_author
WHERE USER_NO = #{user_no}

mapper.select:
/* admin.user.selectFindIdbyName */
SELECT A.USER_ID
       ,A.USER_NM
       ,DATE_FORMAT(A.REGIST_DT, '%Y.%m.%d') AS REGIST_DT 
FROM op_user A
WHERE A.USER_NM	=#{user_nm}
AND   A.CTTPC			=#{cttpc}

mapper.insert:
/* admin.user.insertAuthUser */
INSERT INTO op_user_author (
	USER_NO
	, AUTHOR_ID
	, REGISTER_NO
	, REGIST_DT
) VALUES (
	#{user_no}
	, #{author_id}
	, #{ss_user_no}
	, CURRENT_TIMESTAMP
)
